def Lf(expr):
    # Liouville operator acting on expr in 1D: -d/dx ( f * expr)
    return -sp.diff(f(x)*expr, x)

def Delta(expr):
    return sp.diff(expr, x, 2)

def ad(opA, opB, expr):
    # [opA,opB] acting on expr
    return opA(lambda e: opB(e))(expr) - opB(lambda e: opA(e))(expr)

# Build nested commutator ad_{Delta}^{m}(Lf)
def nested(m, expr):
    if m==0:
        return Lf(expr)
    else:
        return ad(lambda e: Delta(e), lambda e: nested(m-1, e), expr)

for m in range(0,4):
    res = nested(m, g(x))
    # expand
    res = sp.simplify(sp.expand(res))
    print(m, res)

